import './ContactMe.css';

export default function ContactMe(){
// 
  const [subject, setSubject] = React.useState("");
  const [content, setContent] = React.useState("");
  const [address, setAddress] = React.useState("");

  const handleChange1 = (event) =>{
    setSubject(event.target.value);
  }
  const handleChange2 = (event) =>{
    setContent(event.target.value);
  }
  const handleChange3 = (event) =>{
    setAddress(event.target.value);
  }


  const handleSubmit = (event) => {
    if (subject) setSubject(subject);
    if (content) setContent(content);
    if (address) setAddress(address);

    setSubject("");
    setContent("");
    setAddress("");
    event.preventDefault();
  }

// 
  return(
    <div class="container">
      <div class="contactpage--form">
        <form class="contact--form" method="post" onSubmit={handleSubmit} action="http://localhost:3002/messages">

          <h1>CONTACT US!</h1>

          <input required onChange={handleChange1}
          value={subject}
            type="text"
            id="subject"
            name="subject"
            placeholder="subject"
          />

          <textarea 
            required onChange={handleChange2}
            value={content} 
            id="content" 
            name="content" 
            placeholder="content of the message" 
            rows="20">
          </textarea>

          <input 
            required onChange={handleChange3}
            value={address}
            type="email" 
            name="email" 
            placeholder="email address" />

          <button type="submit">SUBMIT</button>

        </form>
      </div>
      <div class="contactpage--photo">

      </div>
    </div>
  );
}
