import './Movie.css'
import React, { useEffect, useState } from "react";

export default function Movie(){
    const [productCount, setProductCount] = useState(1);
    const [product, setProduct] = useState(null);

    function handleClickN(event) {
        setProductCount((cnt) => cnt + 1);
        console.log(productCount);
    }

    function handleClickP(event) {
        setProductCount((cnt) => cnt - 1);
        console.log(productCount);
    }

    return(
        <div>
            <div className='buttons'>
                <button className='button' onClick={handleClickP}>First Page</button>
                <button className='button' onClick={handleClickP}>Prev Page</button>
                <button className='button' onClick={handleClickN}>Next Page</button>
                <button className='button' onClick={handleClickP}>Last Page</button>
            </div>
        </div>
    );
}
