import './DetailedMovInfo.css';
import { useParams } from 'react-router-dom';
import React, { useEffect, useState } from "react";

export default function DetailedMovInfo(){
    const { movieID } = useParams();

    const [movies, setMovies] = useState([]);

    useEffect(() => {
        let url = `http://localhost:3001/movies/${movieID}`;
        fetch(url)
        .then((res) => { return res.json() })
        .then((data) => {
            setMovies(data);
        });
      }, [movieID]);

      <DetailedMovInfo 
        img={movies.Poster_Link}
        title={movies.Series_Title}
        year={movies.Released_Year}
        certificate={movies.Certificate}
        duration={movies.Runtime}
        genre={movies.Genre}
        rating={movies.IMDB_Rating}
        overview={movies.Overview}
        metascore={movies.Meta_score}
        director={movies.Director}
        star_1={movies.Star1}
        star_2={movies.Star2}
        star_3={movies.Star3}
        star_4={movies.Star4}
        vote={movies.No_of_Votes}
        gross={movies.Gross}
        movid={movies.id}

        key={movies.id}
    />

    console.log(movieID);
    return(
        <div className="detailed-movie-card">
            {/* <img src={props.img} className='movie-img' alt="movie-img" /> */}
            <h1>Hello</h1>
        </div>
    );
}
